/**
 * A helper for OAuth 1.0 header information and signing keys.
 */
export class OAuthHelper {
    /**
     * Intialize the helper
     * @param {string} consumerKey The consumer key
     * @param {string} token The token value
     */
    constructor(consumerKey: string, token: string);
    /** @type {string} */
    consumerKey: string;
    /** @type {string} */
    token: string;
    /** @type {string} */
    nonce: string;
    /** @type {string} */
    signature: string;
    /** @type {string} */
    timestamp: string;
    get parameters(): {
        oauth_consumer_key: string;
        oauth_token: string;
        oauth_nonce: string;
        oauth_timestamp: string;
        oauth_signature_method: string;
        oauth_version: string;
    };
    get header(): string;
    get queryString(): string;
    /**
     * Sign the request with oath headers and sets the signature member of the OauthHelper instance.
     */
    sign(url: any, request: any, consumerSecret: any, tokenSecret: any): void;
}
